import React from "react";
import Img1 from "../assets/img1.jpg";
import Img2 from "../assets/img2.jpg";
import { useNavigate } from "react-router-dom";

function HomePage() {
  const navigate = useNavigate();

  function handleSignUp() {
    navigate("/register");
  }

  document.addEventListener("DOMContentLoaded", function () {
    var redDot = document.createElement("div");
    redDot.classList.add("red-dot");
    document.body.appendChild(redDot);

    var delayedMouseX = -100;
    var delayedMouseY = -100;
    var currentMouseX = -100;
    var currentMouseY = -100;

    function updateMousePosition(event) {
      currentMouseX = event.clientX;
      currentMouseY = event.clientY;
    }

    function updateDelayedMousePosition() {
      delayedMouseX += (currentMouseX - delayedMouseX) * 0.2;
      delayedMouseY += (currentMouseY - delayedMouseY) * 0.2;
      redDot.style.left = delayedMouseX + "px";
      redDot.style.top = delayedMouseY + "px";
      requestAnimationFrame(updateDelayedMousePosition);
    }

    document.addEventListener("mousemove", updateMousePosition);
    requestAnimationFrame(updateDelayedMousePosition);
  });

  return (
    <div className="d-flex flex-column align-items-center justify-content-center vh-100">
      <div className="landingtext text-center">
        Create and share <br></br>design ideas with <br></br>your friends.
        <br></br>
        <button className="signup mb-5" onClick={handleSignUp}>
          Sign up
        </button>
        <span></span>
      </div>

      <div className="container d-flex justify-content-center mx-5">
        <img src={Img1} alt="image1" id="img1" />
        <img src={Img2} alt="image2" id="img2" />
      </div>
    </div>
  );
}

export default HomePage;
