import React, { useState } from "react";
import Img1 from "../assets/img1.jpg";
import Img2 from "../assets/img2.jpg";

const HomePage = () => {
  const [isRotated, setIsRotated] = useState(false);

  const handleRotate = () => {
    setIsRotated(!isRotated);
  };

  return (
    <div className="d-flex flex-column align-items-center justify-content-center vh-100">
      <div className="landingtext text-center">
        Create and share <br></br>design ideas with <br></br>your friends.
        <br></br>
        <button className="signup" onClick={handleSignUp}>
          Sign up
        </button>
      </div>
      <div className="container d-flex justify-content-center mb-3">
        <img
          src={Img1}
          alt="image1"
          id="img1"
          className={`mx-3 ${isRotated ? "rotate-right" : ""}`}
          onMouseEnter={handleRotate}
          onMouseLeave={handleRotate}
        />
        <img
          src={Img2}
          alt="image2"
          id="img2"
          className={`mx-3 ${isRotated ? "rotate-left" : ""}`}
          onMouseEnter={handleRotate}
          onMouseLeave={handleRotate}
        />
      </div>
    </div>
  );
};

export default HomePage;
