import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { Button, Form } from "react-bootstrap";
import { register } from "../utils/api";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [selectedEmoji, setSelectedEmoji] = useState("ðŸ‘¿");
  const history = useHistory();

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleEmojiChange = (event) => {
    setSelectedEmoji(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    try {
      await register(username, password, selectedEmoji);
      history.push("/");
    } catch (error) {
      console.error(error);
      alert("Failed to register!");
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center">
      <Form onSubmit={handleSubmit}>
        <h1 className="mb-4">Register</h1>
        <Form.Group controlId="formUsername">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={handleUsernameChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="formPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="formConfirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmoji">
          <Form.Label>Select Emoji</Form.Label>
          <Form.Control
            as="select"
            value={selectedEmoji}
            onChange={handleEmojiChange}
          >
            <option value="ðŸ‘¿">Devil</option>
            <option value="ðŸ©°">Pink Ballet Shoe</option>
            <option value="ðŸ‘»">Ghost</option>
            <option value="ðŸŒŸ">Shooting Star</option>
          </Form.Control>
        </Form.Group>

        <Button variant="primary" type="submit">
          Register
        </Button>
      </Form>
    </div>
  );
}

export default Register;
