import { useEffect, useState } from "react";
import axios from "axios";

function Redpage() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchAllPosts = async () => {
      const { data } = await axios.get("http://localhost:5002/api/posts/all");

      if (data) {
        setPosts(data.allPosts);
      }
    };

    fetchAllPosts();
  }, []);

  return (
    <div className="redpage redpage-grid">
      {posts.length > 0
        ? posts.map((singlePost) => {
            return (
              <article key={singlePost._id} className="redpost">
                <div className="redpost-image-wrap">
                  <img
                    className="redpost-image"
                    src={singlePost.picture}
                    alt={singlePost.title}
                  />
                  <div className="redpost-overlay">
                    <p>{singlePost.title}</p>
                    <p>{singlePost.description}</p>
                  </div>
                </div>
              </article>
            );
          })
        : ""}
    </div>
  );
}
