import React from "react";
import { useState } from "react";
import axios from "axios";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const Register = () => {
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [selectedEmoji, setSelectedEmoji] = useState(null);

  const onEmojiClick = (event, emojiObject) => {
    setSelectedEmoji(emojiObject);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("/api/user/signup", {
        userName,
        email,
        password,
      });

      console.log(response.data);
      // display success message to user
    } catch (error) {
      console.log(error.response.data);
      setErrorMsg(error.response.data.message);
    }
  };

  return (
    <div className="form-container">
      <div className="flex flex-col items-center">
        <h1 className="text-center mb-4">Register</h1>
        {errorMsg && <div className="error-msg">{errorMsg}</div>}
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <div className="form-group">
          <label className="text-center" id="label" htmlFor="userName">
            Choose your Username
          </label>
          <input
            type="text"
            id="userName"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            className="form-control"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <div className="input">
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="form-control"
              required
            />
            <div className="input-group-append">
              {showPassword ? (
                <span
                  className="cursor-pointer"
                  onClick={() => setShowPassword(false)}
                  style={{ color: "red" }}
                >
                  <FaEyeSlash style={{ color: "red" }} />
                </span>
              ) : (
                <span
                  className="cursor-pointer"
                  onClick={() => setShowPassword(true)}
                  style={{ color: "red" }}
                >
                  <FaEye style={{ color: "red" }} />
                </span>
              )}
            </div>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="emoji-select">Choose an emoji:</label>
          <br />
          <input
            type="radio"
            name="emoji-select"
            id="emoji1"
            value="👨‍💼"
            onChange={(e) => setSelectedEmoji(e.target.value)}
          />
          <label htmlFor="emoji1">👨‍💼</label>
          <br />
          <input
            type="radio"
            name="emoji-select"
            id="emoji2"
            value="👩‍💼"
            onChange={(e) => setSelectedEmoji(e.target.value)}
          />
          <label htmlFor="emoji2">👩‍💼</label>
          <br />
          <input
            type="radio"
            name="emoji-select"
            id="emoji3"
            value="👨‍🎤"
            onChange={(e) => setSelectedEmoji(e.target.value)}
          />
          <label htmlFor="emoji3">👨‍🎤</label>
          <br />
          <input
            type="radio"
            name="emoji-select"
            id="emoji4"
            value="👩‍🎤"
            onChange={(e) => setSelectedEmoji(e.target.value)}
          />
          <label htmlFor="emoji4">👩‍🎤</label>
        </div>
      </div>
      <button className="text-center" type="submit">
        Register
      </button>
    </div>
  );
};

export default Register;
