import React from "react";
import Img1 from "../assets/img1.jpg";
import Img2 from "../assets/img2.jpg";

const HomePage = () => {
  const handleSignUp = () => {};

  var image = document.getElementById("ani");

  document.addEventListener("mousemove", function (event) {
    var x = event.clientX;
    var y = event.clientY;
    var rect = image.getBoundingClientRect();
    var centerX = rect.left + rect.width / 2;
    var centerY = rect.top + rect.height / 2;
    var angle = Math.atan2(y - centerY, x - centerX) * (180 / Math.PI);
    image.style.transform = "rotate(" + angle + "deg)";
    image.style.left = x - rect.width / 2 + "px";
    image.style.top = y - rect.height / 2 + "px";
  });

  return (
    <div className="d-flex flex-column align-items-center justify-content-center vh-100">
      <img src={Img1} alt="ani" id="ani" />
      <div className="landingtext text-center">
        Create and share <br></br>design ideas with <br></br>your friends.
        <br></br>
        <button className="signup mb-5" onClick={handleSignUp}>
          Sign up
        </button>
        <span></span>
      </div>

      <div className="container d-flex justify-content-center mx-5">
        <img src={Img1} alt="image1" id="img1" />
        <img src={Img2} alt="image2" id="img2" />
      </div>
    </div>
  );
};

export default HomePage;
