import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button, Form } from "react-bootstrap";

function Register() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [selectedEmojis, setSelectedEmojis] = useState([]);
  const history = useNavigate();

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleEmojiChange = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedEmojis([...selectedEmojis, value]);
    } else {
      setSelectedEmojis(selectedEmojis.filter((emoji) => emoji !== value));
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    try {
      const response = await axios.post("/api/user/signup", {
        userName: username,
        password,
        emojis: selectedEmojis,
      });

      history("/login");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center">
      <Form onSubmit={handleSubmit}>
        <h1 className="mb-4">Register</h1>
        <Form.Group controlId="formUsername">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={handleUsernameChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="formPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="formConfirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmoji">
          <Form.Label>Select Emojis</Form.Label>
          <div>
            <Form.Check
              type="checkbox"
              label="Devil"
              value="ðŸ‘¹"
              onChange={handleEmojiChange}
              checked={selectedEmojis.includes("ðŸ‘¹")}
            />
            <Form.Check
              type="checkbox"
              label="Pink Ballet Shoe"
              value="ðŸ©°"
              onChange={handleEmojiChange}
              checked={selectedEmojis.includes("ðŸ©°")}
            />
            <Form.Check
              type="checkbox"
              label="Ghost"
              value="ðŸ‘»"
              onChange={handleEmojiChange}
              checked={selectedEmojis.includes("ðŸ‘»")}
            />
            <Form.Check
              type="checkbox"
              label="Explosion"
              value="ðŸ’¥"
              onChange={handleEmojiChange}
              checked={selectedEmojis.includes("ðŸ’¥")}
            />
          </div>
        </Form.Group>

        <button type="submit">Register</button>
      </Form>
    </div>
  );
}

export default Register;
