import { useEffect, useState } from "react";
import axios from "axios";

function Redpage() {
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchAllPhotos = async () => {
      const { data } = await axios.get("http://localhost:5002/api/photos/all");

      if (data) {
        setPhotos(data.allPhotos);
      }
    };

    fetchAllPhotos();
  }, []);

  return (
    <div className="redpage">
      {photos.length > 0
        ? photos.map((photo) => {
            return <img key={photo._id} src={photo.url} alt={photo.caption} />;
          })
        : ""}
    </div>
  );
}

export default Redpage;
