import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaEye, FaEyeSlash } from "react-icons/fa";

function Login() {
  const history = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("/api/users/login", formData);
      const token = response.data.token;
      localStorage.setItem("token", token); // Store the token in localStorage
      history.push("/");
    } catch (error) {
      setErrorMessage(error.response.data.message);
    }
  };

  // Check if token is stored after every refresh
  if (localStorage.getItem("token")) {
    // User is logged in
    // You can redirect to home page or set a flag in state to indicate logged in status
  }

  return (
    <div className="flex flex-col items-center">
      <h1 className="text-center mb-4">Login</h1>
      <form onSubmit={handleFormSubmit} className="mb-4">
        <div>
          <label className="text-center" htmlFor="email">
            Email
          </label>
          <input
            type="email"
            name="email"
            id="email"
            value={formData.email}
            onChange={handleInputChange}
            className="border"
            required
          />
        </div>
        <div className="flex flex-col gap-2">
          <label className="text-center" htmlFor="password">
            Password
          </label>
          <div className="relative">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              id="password"
              value={formData.password}
              onChange={handleInputChange}
              className="border mb-2 mx-2"
              required
            />
            {showPassword ? (
              <FaEyeSlash
                onClick={() => setShowPassword(false)}
                className="absolute cursor-pointer"
                style={{ color: "red" }}
              />
            ) : (
              <FaEye
                onClick={() => setShowPassword(true)}
                className="absolute cursor-pointer"
                style={{ color: "red" }}
              />
            )}
          </div>
        </div>
        {errorMessage && <div>{errorMessage}</div>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
