import React, { useRef, useEffect } from "react";
import Img1 from "../assets/img1.jpg";
import Img2 from "../assets/img2.jpg";

const HomePage = () => {
  const spinnerRef = useRef(null);

  const handleSignUp = () => {};

  useEffect(() => {
    const oval = document.getElementById("oval");
    const center = {
      x: oval.offsetLeft + oval.offsetWidth / 2,
      y: oval.offsetTop + oval.offsetHeight / 2,
    };

    document.addEventListener("mousemove", function (e) {
      const spinner = spinnerRef.current;
      const x = e.clientX - center.x;
      const y = e.clientY - center.y;
      const angle = (Math.atan2(y, x) * 180) / Math.PI;
      spinner.style.transform = `translate(-50%, -50%) rotate(${angle}deg)`;
    });
  }, []);

  return (
    <div className="d-flex flex-column align-items-center justify-content-center vh-100">
      <div className="landingtext text-center">
        <div id="spinner" ref={spinnerRef}>
          20DESIGN 20DESIGN
        </div>
        Create and share <br></br>design ideas with <br></br>your friends.
        <br></br>
        <button className="signup mb-5" onClick={handleSignUp}>
          Sign up
        </button>
        <span></span>
      </div>
      <div className="container d-flex justify-content-center mx-5">
        <svg id="oval" viewBox="0 0 200 100" xmlns="http://www.w3.org/2000/svg">
          <path d="M100,50a50,25 0 1,0 100,0a50,25 0 1,0 -100,0" fill="none" />
        </svg>
        <img src={Img1} alt="image1" id="img1" />
        <img src={Img2} alt="image2" id="img2" />
      </div>
    </div>
  );
};

export default HomePage;
